export class VtPinturaComponent {
  constructor(_formBuilder, _route, _activatedRoute, _ordenesService, _vtPinturaService, _informesRutasService) {
    this._formBuilder = _formBuilder;
    this._route = _route;
    this._activatedRoute = _activatedRoute;
    this._ordenesService = _ordenesService;
    this._vtPinturaService = _vtPinturaService;
    this._informesRutasService = _informesRutasService;
    this.actividades = ['Preparación de la superficie.', 'Calificación del personal.', 'Comprensión de las epecificaciones del proyecto.', 'Comprensión de la ficha técnica del fabricante.', 'Condiciones ambientales.', 'Pintura y color especificados.', ' Equipo de calefacción y/ó deshumidificacion.'];
    this.actividadesDurante = ['Condiciones ambientales.', 'Iluminación.', 'Proporción de la mezcla adecuada (incluido el diluyente).', 'Mezclador de poder para  recubrimiento sedimentado.', 'Tiempo de inducción.', 'Aplicación dentro del tiempo de vida útil de la mezcla.', 'Tamizado del zinc inorgánico.', 'Aplicación de capa franja.', 'Medición de espesor de película humedad.', 'Equipos en buenas condiciones.'];
    this.actividadesDespues = ['Apariencia final del recubrimiento', 'Protección  contra lluvia, polvo, transeúntes, etc.', 'Condiciones ambientales durante el secado.'];
    this.isLinear = false;
    this.normasCriterioBtnSave = true;
    this.implementosUtilizados = []; //DESPUES PINTURA

    this.previoPinturaStatus = false;
    this.actividadesCalificacion = []; //DURANTE PINTURA

    this.durantePinturaStatus = false;
    this.actividadesCalificacionDurante = []; //DESPUES PINTURA

    this.despuesPinturaStatus = false;
    this.actividadesCalificacionDespues = [];
  }

  ngOnInit() {
    this.forms();
    this.onGet();
  }

  forms() {
    this.normasCriterio = this._formBuilder.group({
      equipos: ['', [Validators.required]],
      material_base: ['', Validators.required],
      aplicacion: ['', Validators.required],
      acond_Superficie: ['', Validators.required],
