import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';

//SERVICE

import { OrdenesService } from "../../service/ordenes/ordenes.service";
import { VtPinturaService } from "../../service/data-informes/vt-pintura.service";
import { InformesRutasService } from "../../service/rutas-informes/informes-rutas.service";

import Swal from 'sweetalert2';


@Component({
  selector: 'app-vt-pintura',
  templateUrl: './vt-pintura.component.html',
  styleUrls: ['./vt-pintura.component.css']
})
export class VtPinturaComponent implements OnInit {




  actividades: any = [
    'Preparación de la superficie.',
    'Calificación del personal.',
    'Comprensión de las epecificaciones del proyecto.',
    'Comprensión de la ficha técnica del fabricante.',
    'Condiciones ambientales.',
    'Pintura y color especificados.',
    ' Equipo de calefacción y/ó deshumidificacion.']

  actividadesDurante: any = [
    'Condiciones ambientales.',
    'Iluminación.',
    'Proporción de la mezcla adecuada (incluido el diluyente).',
    'Mezclador de poder para  recubrimiento sedimentado.',
    'Tiempo de inducción.',
    'Aplicación dentro del tiempo de vida útil de la mezcla.',
    'Tamizado del zinc inorgánico.',
    'Aplicación de capa franja.',
    'Medición de espesor de película humedad.',
    'Equipos en buenas condiciones.'
  ]

  actividadesDespues: any = [
    'Apariencia final del recubrimiento',
    'Protección  contra lluvia, polvo, transeúntes, etc.',
    'Condiciones ambientales durante el secado.'
  ]


  isLinear = false;
  normasCriterio !: FormGroup;
  PrevioPintura !: FormGroup;
  DurantePintura !: FormGroup;
  DespuesPintura !: FormGroup;

  normasCriterioId: any
  normasCriterioBtnSave: boolean = true
  implementosUtilizados: any = []

  //DESPUES PINTURA
  previoPinturaStatus: boolean = false
  previoPinturaId: any
  actividadesCalificacion: any = []

  //DURANTE PINTURA
  durantePinturaStatus: boolean = false
  durantePinturaId: any
  actividadesCalificacionDurante: any = []

  //DESPUES PINTURA
  despuesPinturaStatus: boolean = false
  despuesPinturaId: any
  actividadesCalificacionDespues: any = []


  //ELEMENTOS INSPECCIONADOS

  imgSelected: any;
  file!: FileList;

  autoSaveIndex: any


  constructor(private _formBuilder: FormBuilder, private _route: Router,
    private _activatedRoute: ActivatedRoute, private _ordenesService: OrdenesService,
    private _vtPinturaService: VtPinturaService, private _informesRutasService: InformesRutasService
  ) { }

  ngOnInit(): void {
    this.forms()
    this.onGet()
  }

  forms() {
    this.normasCriterio = this._formBuilder.group({
      equipos: ['', [Validators.required]],
      material_base: ['', Validators.required],
      aplicacion: ['', Validators.required],
      acond_Superficie: ['', Validators.required],
      anticorrosivo: ['', Validators.required],
      intermedia: ['', Validators.required],
      acabado: ['', Validators.required],
    });
    this.PrevioPintura = this._formBuilder.group({
      actividad: [''],
      calificacion1: [''],
      calificacion2: [''],
      Observaciones: ['']
    });
    this.DurantePintura = this._formBuilder.group({
      actividad: [''],
      calificacion1: [''],
      calificacion2: [''],
      Observaciones: ['']
    });
    this.DespuesPintura = this._formBuilder.group({
      actividad: [''],
      calificacion1: [''],
      calificacion2: [''],
      Observaciones: ['']
    });

  }

  onGet() {
    this.onGetPrevioPintura()
    this.onGetDurantePintura()
    this.onGetDespuesPintura()
    this.onGetAutoSave()
    this.onGetImplementos();
    this.onGetResumenEditNormasCriterio();
  }

  onGetImplementos() {
    this._activatedRoute.params.subscribe(params => {
      const orden_id = params['orden_id'];

      this._ordenesService.getImplementosOrdenes(orden_id).subscribe((res) => {

        this.implementosUtilizados = res.body
      })

    })
  }

  onGetResumenEditNormasCriterio() {

    this._activatedRoute.params.subscribe(params => {

      const orden_id = params['orden_id'];

      const informe_id = params['informe_id'];

      this._vtPinturaService.getNormasCriterioVtPintura(orden_id, informe_id).subscribe((res) => {

        this.normasCriterioId = res.body._id

        this.normasCriterio.patchValue(res.body)

        this.normasCriterioBtnSave = false


        console.log(this.normasCriterioId)
      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    })
  }

  onSubmitOrEditNormasCriterio() {

    if (this.normasCriterioId) {
      let data = { ...this.normasCriterio.value, _id: this.normasCriterioId }

      this._vtPinturaService.putNormasCriterioVtPintura(data).subscribe((res) => {

        this.onGetResumenEditNormasCriterio()

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 401) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: 'No hay usuarios por favor Agregue uno'
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    } else {
      this._activatedRoute.params.subscribe(params => {

        const orden_id = params['orden_id'];

        const informe_id = params['informe_id'];

        let data = { ...this.normasCriterio.value, orden_id, informe_id }

        this._vtPinturaService.postNormasCriterioVtPintura(data).subscribe((res) => {

        }, error => {

          var errorMessage = <any>error;

          if (errorMessage != null) {
            var body = error.error

            if (error.status === 401) {
              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'info',
                title: 'No hay usuarios por favor Agregue uno'
              })
            } else if (error.status === 500) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: body.message

              })

            } else if (error.status === 0) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: 'Por favor compruebe su conexión de internet'

              })

            }
          }
        })
      })
    }
  }


  //PREVIO PINTURA

  async onAddPrevioPintura() {

    if (this.PrevioPintura.status === 'INVALID') {

    } else {

      let i = this.actividades.indexOf(this.PrevioPintura.value.actividad)

      this.actividades.splice(i, 1);

      this.actividadesCalificacion.push(this.PrevioPintura.value)

      this.PrevioPintura.reset()
    }

  }

  onClickEditPrevioPintura(data: any) {

    if (this.previoPinturaStatus) {
      this.previoPinturaId = data._id
    }

    this.actividades.push(data.actividad)
    this.actividadesCalificacion.splice(data.actividad, 1);
    this.PrevioPintura.patchValue(data)
  }

  onGetPrevioPintura() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._vtPinturaService.getPrevioPintura(orden_id, informe_id).subscribe((res) => {

        this.previoPinturaStatus = true
        this.actividades = []
        this.actividadesCalificacion = res.body

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.previoPinturaStatus = false
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })

    })
  }

  onSubmitPrevioPintura() {

    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;


      let data = {
        actividadesCalificacion: this.actividadesCalificacion,
        orden_id: orden_id,
        informe_id: informe_id
      }

      this._vtPinturaService.postPrevioPintura(data).subscribe((res) => {


      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    })
  }

  onEditarPrevioPintura() {
    if (this.PrevioPintura.value.actividad == '') {

    } else {
      this._vtPinturaService.putPrevioPintura(this.PrevioPintura.value, this.previoPinturaId).subscribe((res) => {
        this.PrevioPintura.reset()
        this.onGetPrevioPintura()
      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    }


  }

  onGetAutoSave() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._informesRutasService.getAutoSaveStepper(orden_id, informe_id).subscribe((res) => {

        this.autoSaveIndex = res.body

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.previoPinturaStatus = false
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })

    })
  }

  setIndex(event: any) {


    if (this.autoSaveIndex) {
      let data = {
        indexStepper: event.selectedIndex
      }

      this._informesRutasService.putAutoSaveStepper(data, this.autoSaveIndex._id).subscribe((res) => {
        this.onGetAutoSave()
      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    } else {
      this._activatedRoute.params.subscribe(params => {
        const { orden_id, informe_id } = params;


        let data = {
          indexStepper: event.selectedIndex,
          orden_id,
          informe_id
        }

        this._informesRutasService.postAutoSaveStepper(data).subscribe((res) => {

        }, error => {

          var errorMessage = <any>error;

          if (errorMessage != null) {
            var body = error.error

            if (error.status === 404) {
              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'info',
                title: body.message
              })
            } else if (error.status === 500) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: body.message

              })

            } else if (error.status === 0) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: 'Por favor compruebe su conexión de internet'

              })

            }
          }
        })

      })
    }
  }

  //DURANTE LA PINTURA


  async onAddDurantePintura() {

    if (this.DurantePintura.status === 'INVALID') {

    } else {

      let i = this.actividadesDurante.indexOf(this.DurantePintura.value.actividad)

      this.actividadesDurante.splice(i, 1);

      this.actividadesCalificacionDurante.push(this.DurantePintura.value)

      this.DurantePintura.reset()
    }

  }

  onClickEditDurantePintura(data: any) {

    if (this.durantePinturaStatus) {
      this.durantePinturaId = data._id
    }

    this.actividadesDurante.push(data.actividad)
    this.actividadesCalificacionDurante.splice(data.actividad, 1);
    this.DurantePintura.patchValue(data)
  }

  onGetDurantePintura() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._vtPinturaService.getdurantePintura(orden_id, informe_id).subscribe((res) => {

        this.durantePinturaStatus = true
        this.actividadesDurante = []
        this.actividadesCalificacionDurante = res.body

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.durantePinturaStatus = false
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })

    })
  }

  onSubmitDurantePintura() {

    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      let data = {
        actividadesCalificacion: this.actividadesCalificacionDurante,
        orden_id: orden_id,
        informe_id: informe_id
      }

      this._vtPinturaService.postdurantePintura(data).subscribe((res) => {


      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    })
  }

  onEditarDurantePintura() {
    if (this.DurantePintura.value.actividad == '') {

    } else {
      this._vtPinturaService.putdurantePintura(this.DurantePintura.value, this.durantePinturaId).subscribe((res) => {
        this.DurantePintura.reset()
        this.onGetDurantePintura()
      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    }


  }


  //DESPUES PINTURA

  onClickEditDespuesPintura(data: any) {

    console.log(data)

    if (this.despuesPinturaStatus) {
      this.despuesPinturaId = data._id
    }

    this.despuesPinturaStatus = true

    this.actividadesDespues.push(data.actividad)
    this.actividadesCalificacionDespues.splice(data.actividad, 1);
    this.DespuesPintura.patchValue(data)
  }

  onClickCancel() {

    this.DespuesPintura.reset()

    this.onGetDespuesPintura()

    this.despuesPinturaStatus = false
  }

  onGetDespuesPintura() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._vtPinturaService.getDespuesPintura(orden_id, informe_id).subscribe((res: any) => {


        res.body.map((data: any) => {

          let i = res.body.indexOf(data.actividad)

          this.actividadesDespues.splice(i, 1);

        })

        this.actividadesCalificacionDespues = res.body

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.durantePinturaStatus = false
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
