import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Select2OptionData } from "ng-select2";
import { Options } from "select2";

//service
import { ProyectosService } from "../../service/proyectos/proyectos.service";
import { ClientesService } from "../../service/clientes/clientes.service";
import { SocketService } from '../../service/sockets/socket.service';

//models
import { NuevoProyecto, EditProyecto } from "../../models/proyectos.models";

import { environment } from "../../../environments/environment";

import Swal from 'sweetalert2';

declare let $: any;

@Component({
  selector: 'app-proyectos',
  templateUrl: './proyectos.component.html',
  styleUrls: ['./proyectos.component.css'],
  providers: [ProyectosService, ClientesService, SocketService]
})
export class ProyectosComponent implements OnInit {

  public optionsAdd!: Options
  public optionsEdit!: Options

  public ciudades!: Array<Select2OptionData>

  public url: any
  private token: any

  public remoteSearchOptions: any

  public proyectos: any = [];
  public nuevoProyecto: any;
  public clientes: any = [];
  public editProyecto: any;
  public errorMessage: any;
  public errorMessageEdit: any;

  constructor(
    private _serviceProyecto: ProyectosService,
    private _serviceCliente: ClientesService,
    private _socketService: SocketService,
    private _router: Router
  ) {
    this.nuevoProyecto = new NuevoProyecto('', '', '', '')
    this.editProyecto = new EditProyecto('', '', '', '', '')
    this.url = environment.url;
    this.token = localStorage.getItem('token');
  }

  ngOnInit(): void {
    this.getProyectos();
    this.event();
    this.WindowsOnLoad()
  }
  event() {

    this._socketService.socketEvent('server:proyectos').subscribe((res: any) => {

      if (res.status == 200 && res.event == 'server:proyectos') {
        this.getProyectos();
      }

    })

    this._socketService.socketEvent('server:clientes').subscribe((res: any) => {

      if (res.status == 200 && res.event == 'server:clientes') {

      }

    })
  }

  getProyectos() {

    this._serviceProyecto.getProyectos().subscribe((res: any) => {

      if (res.status === 200) {
        this.proyectos = res.body
      }
    },
      (error) => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 401) {
            this.errorMessage = body.body.message
          }

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'info',
              title: body.body.message,
            });

          } else if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: body.body.message,
            });

          } else if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet',
            });
          }
        }
      }
    );


  }

  WindowsOnLoad() {

    this.optionsAdd ={
      minimumInputLength: 3,
      ajax: {
        url: `${this.url}/api/v1/getClientesSelect`,
        headers: { 'Authorization': this.token },
        type: 'GET',
        dataType: 'json',
        data: (params) => {
          const searchOptions = this.remoteSearchOptions;
          if (!!(searchOptions) === false) {
            return null;
          }
          searchOptions.filterByColumnValue = params.term;
          searchOptions.offset = params.page || 0;
          return searchOptions;
        },
        processResults: (data, params) => {
          // parse the results into the format expected by Select2
          // since we are using custom formatting functions we do not need to
          // alter the remote JSON data, except to indicate that infinite
          // scrolling can be used
          params.page = params.page || 1;

          // change search offset
          this.remoteSearchOptions.offset = data.length;
          console.log(data)
          return {
            results: data.body,
            pagination: {
              more: (params.page * this.remoteSearchOptions.pageSize) <= data.length
            }
          };
        },
        cache: true
      },
      escapeMarkup: function (markup) { return markup; },
      dropdownParent: $('#exampleModal')
    }



/*     $('#selectCliente').select2(
      {
        minimumInputLength: 3,
        ajax: {
            url: `${this.url}/api/v1/getClientesSelect`,
            dataType: 'json',
            quietMillis: 250,
            data: (term:any, page:any) => { // page is the one-based page number tracked by Select2
                return {
                    q: term, //search term
                    page: page // page number
                };
            },
            results: (data:any, page:any) => {

              console.log(data.body)
                const more = (page * 30) < data.total_count; // whether or not there are more results available

                // notice we return the value of more so Select2 knows if more results can be loaded
                return { results: data.body, more: more };
            }
        },
        escapeMarkup: (markup: any) => { return markup; },
        language: "ES",
        dropdownParent: $('#exampleModal')
      }
    )
    $('.select2Edit').select2(
      {
        placeholder: {
          id: '-1', // the value of the option
          text: "--Seleccione una Ciudad--"
        },
        allowClear: true,
        language: "es",
        dropdownParent: $('#exampleModal2')
      }) */
  }

  getSearchClientes(params: any, data: any) {


    console.log(params, data)

    /*     this._serviceCliente.getClientesSelect().subscribe((res: any) => {
          if (res.status === 200) {
            this.clientes = res.body
            this.WindowsOnLoad(res.body)
          }

        }) */


  }

  addProyecto() {

    this._serviceProyecto.addProyecto(this.nuevoProyecto).subscribe((res: any) => {

      if (res.status === 200) {
        Swal.fire({
          title: '¡El proyecto se ha guardado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });
        this.errorMessage = null

        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-add'
        )[0] as HTMLElement;
        element.click();

        this.getProyectos();
        this.nuevoProyecto = new NuevoProyecto('', '', '', '')

        this.errorMessage = false
      }
    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error
        if (error.status === 401) {
          this.errorMessage = body.body.message
        }

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })
  }

  getProyecto(id: any) {

    this._serviceProyecto.getProyecto(id).subscribe((res: any) => {

      if (res.status === 200) {
        this.editProyecto = res.body[0]
      }


    })



  }

  UpdateProyecto() {

    this._serviceProyecto.updateProyecto(this.editProyecto).subscribe((res: any) => {


      if (res.status === 200) {
        this.getProyectos();
        Swal.fire({
          title: '¡El proyecto se ha modificado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });
        this.errorMessageEdit = null

        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-edit'
        )[0] as HTMLElement;
        element.click();

        this.getProyectos();
        this.editProyecto = new EditProyecto('', '', '', '', '')
      }
    },
      (error) => {
        var errorMessageEdit = <any>error;

        if (errorMessageEdit != null) {
          var body = error.error;

          if (error.status === 401) {
            this.errorMessageEdit = body.body.message
          }

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'info',
              title: body.body.message,
            });
          }

          if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: body.body.message,
            });
          }

          if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet',
            });
          }
        }
      });

  }

  deleteProyecto(id: any) {
    Swal.fire({

      title: "¿Está seguro de eliminar el proyecto?",
      text: "¡Si no lo está puede cancelar la accíon!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      cancelButtonText: 'Cancelar',
      confirmButtonText: 'Si, eliminar proyecto!'

    }).then((result) => {

      if (result.value) {

        this._serviceProyecto.deleteProyecto(id).subscribe((res: any) => {

          if (res.status === 200) {
            Swal.fire({
              title: '¡El Proyecto se ha eliminado correctamente!',
              icon: 'success',
              confirmButtonText: 'Cerrar',
            });
            this.getProyectos();
          }
        },
          error => {
            var errorMessage = <any>error;

            if (errorMessage != null) {
              var body = error.error

              if (error.status == 404) {
                Swal.fire({
                  title: body.body.message,
                  icon: 'warning',
                  confirmButtonText: 'Cerrar',
                });
              }

              if (error.status == 500) {

                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: body.body.message

                })

              } else if (error.status == 0) {
                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: 'Por favor compruebe su conexión de internet'

                })
              }
            }
          });
      }
    })
  }
