    this._http = _http;
    this._socketService = _socketService;
    this._serviceOrdenes = _serviceOrdenes;
    this._serviceUsuarios = _serviceUsuarios;
    this._proyectosService = _proyectosService;
    this._subproyectosService = _subproyectosService;
    this._serviceImplemento = _serviceImplemento;
    /* ORDENES */

    this.ordenes = [];
    this.proyectos = [];
    this.subProyectos = [];
    this.usuariosOrdenesDetalles = [];
    /* USUARIOS */

    this.usuariosOrdenes = [];
    this.usuariosOrdenesHistorial = [];
    this.usuarios = [];
    this.usuariosTable = [];
    this.usuariosModal = [];
    this.newUsuario = {
      fecha_inicial: '',
      fecha_final: '',
      Usuario_id: ''
    };
    this.statusEditAndSubmit = false;
    /* IMPLEMENTOS */

    this.implementosOrdenesHistorial = [];
    this.implementosOrdenes = [];
    this.newImplemento = {
      implemento_id: ''
    };
    this.implementosTable = [];
    this.implementosOrdenesModal = [];

    this.templateResult = state => {
      if (!state.id) {
        return state.text;
      }

      return jQuery("<div class='select2-result-repository clearfix'>" + `
        <div class='select2-result-repository__meta'>
        <div class='select2-result-repository__title' style='color: black; font-weight: 700; word-wrap: break-word; line-height: 1.1; margin-bottom: 4px;'>${state.text}</div>
        <div class='select2-result-repository__description'></div>
        <div class='select2-result-repository__statistics'>
        <div class='select2-result-repository__forks' style='display: inline-block; color: #777; font-size: 15px;'>Codigo N: ${state.codigo_n} </div>
        <div class='select2-result-repository__stargazers' style='display: inline-block; color: #777;  font-size: 15px;'>Serial: ${state.serial}</div>
        </div>
        </div>
        </div>`);
    };

    this.templateResultProyecto = state => {
      if (!state.id) {
        return state.text;
      }

      return jQuery("<div class='select2-result-repository clearfix'>" + `
        <div class='select2-result-repository__meta'>
        <div class='select2-result-repository__title' style='color: black; font-weight: 700; word-wrap: break-word; line-height: 1.1; margin-bottom: 4px;'>${state.text}</div>
        <div class='select2-result-repository__description'></div>
        <div class='select2-result-repository__statistics'>
        <div class='select2-result-repository__forks' style='display: inline-block; color: #777; font-size: 15px;'>Cliente: ${state.cliente} </div>
        </div>
        </div>
        </div>`);
    };

    this.url = environment.url;
    this.NuevaOrden = new NuevaOrden('', '', new Date(), '', '');
    this.EditarOrden = new EditarOrden('', '', '', new Date(), '', 0);
    this.addUsuario = new AddUsuario('', '', '', '');
    this.addImplemento = new AddImplemento('', '');
    this.identity = _serviceUsuarios.getIdentity();
    this.token = localStorage.getItem('token');
  }

  ngOnInit() {
    this.WindowsOnLoad();
    this.getOrdenes();
    this.getContadorOrdenes();
    this.event();
  }

  WindowsOnLoad() {
    let options = {
      minimumInputLength: 2,
      ajax: {
        url: `${this.url}/api/v1/getImplementosSelect`,
        headers: {
          'Authorization': this.token
        },
        type: 'GET',
        dataType: 'json',
        data: params => {
          return {
            implemento: params.term,
            pagina: params.page || 0
          };
        },
        processResults: (data, params) => {
          params.page = params.page || 0;
          return {
            results: data.body.data,
            pagination: {
              more: 30 <= data.body.total_count
            }
          };
        },
        cache: true
      },
      templateResult: this.templateResult,
      escapeMarkup: function (markup) {
        return markup;
      }
    };
    this.optionsAdd = Object.assign(Object.assign({}, options), {
      dropdownParent: $('#exampleModal1'),
      width: '100%'
    });
    this.optionsAddProyecto = {
      minimumInputLength: 2,
      dropdownParent: $('#exampleModal1'),
      width: '100%',
      ajax: {
        url: `${this.url}/api/v1/getProyectosSelect`,
        headers: {
          'Authorization': this.token
        },
        type: 'GET',
        dataType: 'json',
        data: params => {
          return {
            proyecto: params.term,
            pagina: params.page || 0
          };
        },
        processResults: (data, params) => {
          params.page = params.page || 0;
          return {
            results: data.body.data,
            pagination: {
              more: 30 <= data.body.numeroPaginas
            }
          };
        },
        cache: true
      },
      templateResult: this.templateResultProyecto,
      escapeMarkup: function (markup) {
        return markup;
      }
    };
    this.optionsAddImplemento = Object.assign(Object.assign({}, options), {
      dropdownParent: $('#implementosModal'),
      width: '100%'
    });
  }

  event() {
    this._socketService.socketEvent('server:ordenes').subscribe(res => {
      if (res.status == 200 && res.event == 'server:ordenes') {
        this.getOrdenes();
        this.getContadorOrdenes();
      }
    });
  }

  getContadorOrdenes() {
    this._serviceOrdenes.getContadorOrdenes().subscribe(res => {
      this.contadorOrdenes = res.body[0];
    });
  }

  getOrdenes() {
    if (this.identity.perfil == "Operador") {
      var id = this.identity.id;
    } else {
      var id = null;
    }

    this._serviceOrdenes.getOrdenes(id).subscribe(res => {
      if (res.status === 200) {
        this.ordenes = res.body;
      }
    }, error => {
      var errorMessage = error;

      if (errorMessage != null) {
        var body = error.error;

        if (error.status === 401) {
          this.message = body.body.message;
        }

        if (error.status === 404) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          });
          Toast.fire({
            icon: 'info',
            title: body.body.message
          });
          this.ordenes = [];
        } else if (error.status === 500) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          });
          Toast.fire({
            icon: 'error',
            title: body.body.message
          });
        } else if (error.status === 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          });
          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'
          });
        }
      }
    });
  }

  getProyectos() {
    this._proyectosService.getProyectos().subscribe(res => {
      this.proyectos = res.body;
    });
  }

  getSubProyectos(idProyecto) {
    this._subproyectosService.getSubproyectos(idProyecto).subscribe(res => {
      this.subProyectos = res.body;
    }, error => {
      var errorMessage = error;

      if (errorMessage != null) {
        var body = error.error;

        if (error.status === 404) {
          this.subProyectos = [];
          Swal.fire({
            title: `¡No hay subproyectos vinculados a este proyecto! <a href="/sub-proyectos/${idProyecto}">Agregar uno</a>`,
            icon: 'warning',
            confirmButtonText: 'Cerrar'
          });
        } else if (error.status === 500) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          });
          Toast.fire({
            icon: 'error',
            title: body.body.message
          });
        } else if (error.status === 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          });
          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'
          });
        }
      }
    });
  } //


  getUsuariosSelect() {
    this._serviceUsuarios.getUsuariosSelect().subscribe(res => {
      this.usuarios = res.body;
    });
  }

  getUsuarioOrdenes(id) {
    this.addUsuario.orden_id = id;

    this._serviceOrdenes.getUsuariosOrdenes(id).subscribe(res => {
      this.usuariosOrdenes = res.body;
    }, error => {
      var errorMessage = error;

      if (errorMessage != null) {
        var body = error.error;

        if (error.status === 404) {
