    this._subproyectosService.getSubproyectos(idProyecto).subscribe((res: any) => {

      this.subProyectosEditar = res.body

    },
      (error) => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 404) {
            this.subProyectos = []

            Swal.fire({
              title: `¡No hay subproyectos vinculados a este proyecto! <a href="/sub-proyectos/${idProyecto}">Agregar uno</a>`,
              icon: 'warning',
              confirmButtonText: 'Cerrar',
            });

          } else if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: body.body.message,
            });
          } else if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet',
            });
          }
        }
      })
  }

  updateOrden() {

    this._serviceOrdenes.updateOrden(this.EditarOrden).subscribe((res: any) => {

      if (res.status === 200) {

        Swal.fire({
          title: '¡La Orden se ha modificado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });

        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-edit'
        )[0] as HTMLElement
        element.click();

        this.getProyectos()
        this.EditarOrden = new EditarOrden('', '', '', new Date(), '')

      }
    },
      (error) => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 401) {
            this.message = body.body.message
          }

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'info',
              title: body.body.message,
            });
          }

          if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: body.body.message,
            });
          }

          if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet',
            });
          }
        }
      })
  }

  getDetallesOrden(id: any) {
    this._serviceOrdenes.getDetallesOrdenes(id).subscribe((res: any) => {
      this.detallesOrdenes = res.body[0]
      this.usuariosOrdenesDetalles = res.body[0].Usuarios_Json
    })
  }
