import { Component, OnInit } from '@angular/core';
import { SocketProviderConnect } from '../../service/socket.io';

import { ImplementosService } from '../../service/implementos/implementos.service';
import { TipoImplementoService } from "../../service/tipo_implemento/tipo-implemento.service";

import {
  NuevoImplemento,
  EditarImplemento,
} from '../../models/implementos.models';

import Swal from 'sweetalert2';

@Component({
  selector: 'app-implementos',
  templateUrl: './implementos.component.html',
  styleUrls: ['./implementos.component.css'],
  providers: [SocketProviderConnect, ImplementosService, TipoImplementoService],
})
export class ImplementosComponent implements OnInit {
  public implementos: any = [];
  public socket: any;
  public nuevoImplemento: any;
  public editarImplemento: any;
  public message: any;
  public dataserial: any = [];
  public serialdata: string = '';
  public modelodata: string = '';
  public seriales: any = [];
  public errorSerialMessage: any;
  public errorSerialImplemento: any;
  public tipoImplementos: any = [];
  public newSerial = {
    serial: '',
    implemento_id: ''
  }

  constructor(
    private socketService: SocketProviderConnect,
    private _serviceImplementos: ImplementosService,
    private _serviceTipoImplementos: TipoImplementoService
  ) {
    this.socket = this.socketService.setupSocketConnection();
    this.editarImplemento = new EditarImplemento('', '', '');
    this.nuevoImplemento = new NuevoImplemento('', '');
  }

  ngOnInit(): void {
    this.getImplementos();
    this.event();
  }

  async Agregar_Serial() {

    if (this.serialdata === '' || this.modelodata === '') {
      this.errorSerialImplemento = 'Ingrese un serialo un modelo valido'
    } else {


      if (this.dataserial.indexOf(this.serialdata) == 0) {
        this.errorSerialImplemento = 'Ya hay un serial registrado'

      } else {

        let data = {serial: this.serialdata, modelo:this.modelodata}

        await this.dataserial.push(data);
        console.log(this.dataserial)
        this.errorSerialImplemento = null;
        this.serialdata = ''        
        this.modelodata = ''
      }
    }
  }

  remover(dataserial: any) {
    this.dataserial.splice(dataserial, 1);
  }

  getImplementos() {
    this._serviceImplementos.getImplementos().subscribe(
      (res: any) => {
        if (res.status === 200) {
          this.implementos = res.body;
        }
      },
      (error) => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 401) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'info',
              title: 'No hay usuarios por favor Agregue uno',
            });
          } else if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: body.message,
            });
          } else if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px',
            });

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet',
            });
          }
        }
      }
    );
  }

  tiposImplementos() {
    this._serviceTipoImplementos.getTiposImplementos().subscribe((res: any) => {

      if (res.status == 200) {
        this.tipoImplementos = res.body
      }
    })
  }

  event() {
    this.socket.on('server:implementos', (res: any) => {
      if (res.status === 200) {
        this.getImplementos();
      }
    });
    this.socket.on('server:tipo_implemento', (res: any) => {
      if (res.status === 200) {
        this.tiposImplementos();
      }
    });
    this.socket.on('connect_error', (err: any) => {
      console.log(`connect_error due to ${err.message}`);
    });
  }

  SaveImplemento() {
    const data = {
      ...this.nuevoImplemento,
      serial: this.dataserial,
    };
    this._serviceImplementos.addImplementos(data).subscribe((res: any) => {
      if (res.status === 200) {
        Swal.fire({
          title: '¡El usuario se ha guardado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });
        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-add'
        )[0] as HTMLElement;
        element.click();
        this.nuevoImplemento = new NuevoImplemento('', '');
        this.message = null;
        this.getImplementos();
        this.dataserial = [];
      }

      if (res.status === 401) {
        this.message = res.body.message;
      }

      if (res.status === 404) {
        Swal.fire({
          title: res.body.message,
          icon: 'warning',
          confirmButtonText: 'Cerrar',
        });
      }

      if (res === 500) {
        Swal.fire({
          title: res.body.message,
          icon: 'error',
          confirmButtonText: 'Cerrar',
        });
      }
    });
  }

  getSeriales(id: any) {

    this.newSerial.implemento_id = id

    this._serviceImplementos.getSeriales(id).subscribe((res: any) => {
      if (res.status === 200) {
        this.seriales = res.body;
        this.errorSerialMessage = null
      }

    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error

        if (error.status == 404) {
          this.errorSerialMessage = body.body.message
          this.seriales = [];
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    });
  }

  deleteImplemento(id: any) {
    this._serviceImplementos.deleteImplemento(id).subscribe((res: any) => {

      if (res.status === 200) {
        Swal.fire({
          title: '¡El Implemento se ha eliminado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });
      }

      if (res.status === 401) {
        Swal.fire({
          title: res.body.message,
          icon: 'warning',
          confirmButtonText: 'Cerrar',
        });

      }

      if (res === 500) {
        Swal.fire({
          title: res.body.message,
          icon: 'error',
          confirmButtonText: 'Cerrar',
        });
      }


    });
  }

  deleteSerial(id: any, implemento_id: any) {

    this._serviceImplementos.deleteSerial(id).subscribe((res: any) => {

      if (res.status === 200) {
        this.getSeriales(implemento_id)
        Swal.fire({
          title: '¡El Serial se ha eliminado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });
      }

    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })
  }

  addSerial() {

    if (this.newSerial.serial === '') {
      this.errorSerialMessage = 'Ingrese un serial valido'

    } else {
      this.errorSerialMessage = null
      this._serviceImplementos.addSerial(this.newSerial).subscribe((res: any) => {

        if (res.status === 200) {
          this.getSeriales(this.newSerial.implemento_id)
          Swal.fire({
            title: '¡El serial se ha guardado correctamente!',
            icon: 'success',
            confirmButtonText: 'Cerrar',
          });
          this.newSerial.implemento_id, this.newSerial.serial = ''
          this.errorSerialMessage = null;
        }

      }, error => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 401) {
            this.message = body.body.message;
          }

          if (error.status === 404) {
            Swal.fire({
              title: body.body.message,
              icon: 'warning',
              confirmButtonText: 'Cerrar',
            });
          }

          if (error.status == 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.body.message

            })

          } else if (error.status == 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })
          }
        }
      })
    }
  }

  getImplemento(id: any) {

    this._serviceImplementos.getImplemento(id).subscribe((res: any) => {

      if (res.status === 200) {

        this.editarImplemento = res.body[0]

      }
    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })
  }

  updateImplemento() {

    this._serviceImplementos.updateImplemento(this.editarImplemento).subscribe((res: any) => {

      if (res.status === 200) {
        this.getImplementos()
        Swal.fire({
          title: '¡El implemento se ha modificado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });

        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-edit'
        )[0] as HTMLElement
        element.click();

        this.editarImplemento = new EditarImplemento('', '', '')

      }
    })
  }
}
