    this._serviceOrdenes.getImplemntosOrdenesHistorial(id).subscribe((res: any) => {
      this.implementosOrdenesHistorial = res.body
    },
      error => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status == 404) {
