              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })
          }
        }
      });
  }

  saveImplemento() {

    this._serviceOrdenes.addImplementoOrdenes(this.addImplemento).subscribe((res: any) => {

      if (res.status === 200) {
        Swal.fire({
          title: 'El Implemento se ha guardado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });

        this.getImplementosOrdenes(this.addImplemento.orden_id);
        this.addImplemento.implemento_id = ''

        this.messageAddImplemento = false

      }
    }, error => {
      var message = <any>error;

      if (message != null) {
        var body = error.error
        if (error.status === 401) {
          this.messageAddImplemento = body.body.message
        }

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })
  }

  deleteImplementoOrden(id: any) {
    Swal.fire({
      title: "¿Está seguro de eliminar el implmento?",
      text: "¡Si no lo está puede cancelar la accíon!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      cancelButtonText: 'Cancelar',
      confirmButtonText: 'Si, eliminar Implemento!'

    }).then((result) => {

      if (result.value) {

        this._serviceOrdenes.deleteImplemento(id).subscribe((res: any) => {

          if (res.status === 200) {
            Swal.fire({
              title: '¡El implemento se ha eliminado correctamente!',
              icon: 'success',
              confirmButtonText: 'Cerrar',
            });

            this.getImplementosOrdenes(this.addImplemento.orden_id);

          }
        },
          error => {
            var errorMessage = <any>error;

            if (errorMessage != null) {
              var body = error.error

              if (error.status == 404) {
                Swal.fire({
                  title: body.body.message,
                  icon: 'warning',
                  confirmButtonText: 'Cerrar',
                });
              }

              if (error.status == 500) {

                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: body.body.message

                })

              } else if (error.status == 0) {
                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: 'Por favor compruebe su conexión de internet'

                })
              }
            }
          });
      }
    })
  }

