export class UltrasonidoComponent {
  constructor(_formBuilder, _route, _activatedRoute, _ordenesService, _adherenciaComponent, _ultrasonidoService, _informesRutasService, _opcionesInformesService) {
    this._formBuilder = _formBuilder;
    this._route = _route;
    this._activatedRoute = _activatedRoute;
    this._ordenesService = _ordenesService;
    this._adherenciaComponent = _adherenciaComponent;
    this._ultrasonidoService = _ultrasonidoService;
