    this._opcionesInformesService = _opcionesInformesService;
    this.quillModules = {
      toolbar: [['bold', 'italic', 'underline'], [{
        align: []
      }, {
        list: 'ordered'
      }, {
        list: 'bullet'
      }], ['clean']]
    };
    this.NormasList = ['AWS D1.1 2020', 'AWS D1.2 2003', 'AWS D1.3 2008', 'AWS D1.4 2005', 'AASHTO/AWS D1.5 2010', 'AWS D1.6 2007', 'AWS D1.7 2010', 'AWS D1.8 2009', 'AWS D1.9 2007', 'AWS D14.3 2010', 'ASME SECCIÓN V 2010', 'ASME SECCIÓN IX 2010', 'ASME B31.1 2010', 'ASME B31.3 2010', 'ASME B31.4 2006', 'ASME B31.5 2001', 'ASME B31.8 2010', 'ASME B31.9 2008', 'ASME B31G 2009', 'ASTM E 186', 'API 1104']; //OPCIONES MATERIALES BASES

    this.MaterialesBase = [];
    this.ModoAplicacion = this._adherenciaComponent.ModosdeAplicacion;
    this.filteredMaterialesMulti = new ReplaySubject(1);
    this.materialesMultiFilterCtrl = new FormControl();
    this._onDestroy = new Subject();
    this.implementosOrdenes = [];
    this.isLinear = false;
    this.descripcionItemArray = [];
    this.descripcionArray = [];
    this.firmaRevisaImg = '';
    this.firmaRevisoStatus = false;
    this.firmaElaboroImg = '';
    this.firmaElaboroStatus = false;
    this.RegistroFotos = [];
    this.showFlagRegistroFotos = false;
    this.selectedImageIndexRegistroFotos = -1;
    this.imageObjectRegistroFotos = [];
    this.url = environment.url;

    this._activatedRoute.params.subscribe(params => {
      this.orden_id = params['orden_id'];
      this.informe_id = params['informe_id'];
    });
  }

  ngOnInit() {
    this.onGetAutoSave();
    this.onGetDetallesOrden();
    this.onGetUltrasonido();
    this.forms();
    this.getImplementosOrdenes();
    this.getFirmaReviso();
    this.getFirmaElaboro();
    this.onGetOpciones();
  }

  onGetOpciones() {
    this._opcionesInformesService.getOpcionesOrdenes(this.informe_id).subscribe(res => {
      const result = res.body.filter(opciones => opciones.name === 'material_base');
      this.MaterialesBase = result[0].data;
      this.opcionesMaterialBaseId = result[0]._id;
      this.onInitFilter();
    });
  }

  onInitFilter() {
    // set initial selection
    // load the initial bank list
    this.filteredMaterialesMulti.next(this.MaterialesBase.slice()); // listen for search field value changes

    this.materialesMultiFilterCtrl.valueChanges.pipe(takeUntil(this._onDestroy)).subscribe(() => {
      this.filterMaterialMulti();
    });
  }

  ngAfterViewInit() {
    this.setInitialValue();
  }

  ngOnDestroy() {
    this._onDestroy.next();

    this._onDestroy.complete();
  }

  setInitialValue() {
    this.filteredMaterialesMulti.pipe(take(1), takeUntil(this._onDestroy)).subscribe(() => {
      this.multiSelect.compareWith = (a, b) => a && b && a === b;
    });
  }

  filterMaterialMulti() {
    if (!this.MaterialesBase) {
      return;
    } // get the search keyword


    let search = this.materialesMultiFilterCtrl.value;

    if (!search) {
      this.filteredMaterialesMulti.next(this.MaterialesBase.slice());
      return;
    } else {
      search = search.toLowerCase();
    } // filter the banks


    this.filteredMaterialesMulti.next(this.MaterialesBase.filter(materialBase => materialBase.toLowerCase().indexOf(search) > -1));
  }

  onClickAddMaterialBase() {
    const data = {
      _id: this.opcionesMaterialBaseId,
      data: this.materialesMultiFilterCtrl.value
    };

    this._opcionesInformesService.addOpcionesOrdenes(data).subscribe(res => {
      Swal.fire({
        title: '¡Se agrego correctamente la opción!',
        icon: 'success',
        confirmButtonText: 'Cerrar'
      });
      this.onGetOpciones();
    });
  }

  forms() {
    this.Normas = this._formBuilder.group({
      criterio_aceptacion: ['', [Validators.required]],
      procedimiento: ['', [Validators.required]]
    });
    this.Juntas = this._formBuilder.group({
      juntas: ['']
    });
    this.ElementosInspeccionados = this._formBuilder.group({
