      estampe: ['', [Validators.required]],
      decibeles_a: ['', [Validators.required]],
      decibeles_b: ['', [Validators.required]],
      decibeles_c: ['', [Validators.required]],
      decibeles_d: ['', [Validators.required]],
      dist: ['', [Validators.required]],
      prof_mm: ['', [Validators.required]],
      dist_y_mm: ['', [Validators.required]],
      dist_x_mm: ['', [Validators.required]],
      eval: ['', [Validators.required]]
    });
    this.observaciones = this._formBuilder.group({
      observaciones: ['', [Validators.required]]
    });
  }

  getImplementosOrdenes() {
    this._activatedRoute.params.subscribe(params => {
      // this.addImplemento.orden_id = orden_id
      const {
        orden_id,
        informe_id
      } = params;

      this._ordenesService.getImplementosOrdenes(orden_id).subscribe(res => {
        this.implementosOrdenes = res.body; // console.log(this.implementosOrdenes)
      }, error => {
        var errorMessage = error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 404) {
            this.implementosOrdenes = [];
          }

          if (error.status == 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: body.body.message
            });
          } else if (error.status == 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'
            });
          }
        }
      });
    });
  }

