      const data = Object.assign({}, params);

      this._ultrasonidoService.postUltrasonidoInit(data).subscribe(data => {
        this.onGetUltrasonido();
      }, error => {
        var errorMessage = error;

        if (errorMessage != null) {
          var body = error.error;

