sourcesContent:
import { Component, ElementRef, OnInit, ViewChild, Inject, Injectable } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormControl } from '@angular/forms';
import { Router, ActivatedRoute, NavigationEnd } from '@angular/router';
import { ReplaySubject, Subject } from 'rxjs';
import { take, takeUntil } from 'rxjs/operators';
import { MatSelect } from '@angular/material/select';


import { AdherenciaComponent } from '../../components/adherencia/adherencia.component';

import { InformesRutasService } from "../../service/rutas-informes/informes-rutas.service";
import { OrdenesService } from "../../service/ordenes/ordenes.service";
import { UltrasonidoService } from 'src/app/service/ultrasonido/ultrasonido.service';
import { OpcionesInformesService } from "../../service/opciones-informes/opciones-informes.service"
import { environment } from "../../../environments/environment";

import Swal from 'sweetalert2';

@Component({
  selector: 'app-ultrasonido',
  templateUrl: './ultrasonido.component.html',
  styleUrls: ['./ultrasonido.component.css'],
  providers: [AdherenciaComponent]
})
export class UltrasonidoComponent implements OnInit {
  url: string;

  orden_id: any
  informe_id: any

  @ViewChild('formFileSmRegistroFotos')
  myInputVariableRegistroFotos!: ElementRef;

  quillModules: any = {
    toolbar: [
      ['bold', 'italic', 'underline'],
      [{ align: [] }, { list: 'ordered' }, { list: 'bullet' }],
      ['clean']
    ]
  };

  NormasList = [
    'AWS D1.1 2020',
    'AWS D1.2 2003',
    'AWS D1.3 2008',
    'AWS D1.4 2005',
    'AASHTO/AWS D1.5 2010',
    'AWS D1.6 2007',
    'AWS D1.7 2010',
    'AWS D1.8 2009',
    'AWS D1.9 2007',
    'AWS D14.3 2010',
    'ASME SECCIÓN V 2010',
    'ASME SECCIÓN IX 2010',
    'ASME B31.1 2010',
    'ASME B31.3 2010',
    'ASME B31.4 2006',
    'ASME B31.5 2001',
    'ASME B31.8 2010',
    'ASME B31.9 2008',
    'ASME B31G 2009',
    'ASTM E 186',
    'API 1104'
  ]


  //OPCIONES MATERIALES BASES

  MaterialesBase: any = []
  ModoAplicacion = this._adherenciaComponent.ModosdeAplicacion;

  filteredMaterialesMulti: ReplaySubject<[]> = new ReplaySubject<[]>(1);
  materialesMultiFilterCtrl: FormControl = new FormControl();
  protected _onDestroy = new Subject<void>();
  opcionesMaterialBaseId: any

  @ViewChild('multiSelect') multiSelect!: MatSelect;
  public implementosOrdenes: any = []
  public addImplemento: any


  detallesOrden: any
  detallesInforme: any

  ultrasonidoId: any

  isLinear = false;
  Normas!: FormGroup;
  Juntas!: FormGroup;
  ElementosInspeccionados!: FormGroup;
  Equipo_Utilizado!: FormGroup;
  Palpador!: FormGroup;
  Soldadura!: FormGroup;
  Materiales!: FormGroup;
  descripcion!: FormGroup
  descripcionItem!: FormGroup
  observaciones!: FormGroup

  descripcionItemArray: any = []

  descripcionArray: any = []

  //firmas
  canvas: any
  firmaRevisaImg: String = ''
  firmaRevisoStatus: boolean = false


  firmaElaboroImg: String = '';
  firmaElaboroStatus: boolean = false


  autoSaveIndex: any
  statusAutoSave: any

  //REGISTRO FOTOGRÁFICO

  imgSelectedRegistroFotos: any;
  fileRegistroFotos!: File;
  RegistroFotos: any = []
  showFlagRegistroFotos: boolean = false;
  selectedImageIndexRegistroFotos: number = -1;
  currentIndexRegistroFotos: any
  imageObjectRegistroFotos: Array<object> = []
  RegistroFotosForm!: FormGroup



  constructor(
    private _formBuilder: FormBuilder, private _route: Router,
    private _activatedRoute: ActivatedRoute,
    private _ordenesService: OrdenesService,
    private _adherenciaComponent: AdherenciaComponent,
    private _ultrasonidoService: UltrasonidoService,
    private _informesRutasService: InformesRutasService,
    private _opcionesInformesService: OpcionesInformesService
  ) {

    this.url = environment.url;
    this._activatedRoute.params.subscribe(params => {
      this.orden_id = params['orden_id'];
      this.informe_id = params['informe_id'];

    })
  }

  ngOnInit(): void {
    this.onGetAutoSave()
    this.onGetDetallesOrden();
    this.onGetUltrasonido();
    this.forms()
    this.getImplementosOrdenes()
    this.getFirmaReviso()
    this.getFirmaElaboro()

    this.onGetOpciones()
  }


  onGetOpciones() {
    this._opcionesInformesService.getOpcionesOrdenes(this.informe_id).subscribe((res) => {

      const result = res.body.filter((opciones: any) => opciones.name === 'material_base')

      this.MaterialesBase = result[0].data

      this.opcionesMaterialBaseId = result[0]._id

      this.onInitFilter()

    })
  }

  onInitFilter() {
    // set initial selection


    // load the initial bank list
    this.filteredMaterialesMulti.next(this.MaterialesBase.slice());

    // listen for search field value changes
    this.materialesMultiFilterCtrl.valueChanges
      .pipe(takeUntil(this._onDestroy))
      .subscribe(() => {
        this.filterMaterialMulti();
      });
  }

  ngAfterViewInit() {
    this.setInitialValue();
  }

  ngOnDestroy() {
    this._onDestroy.next();
    this._onDestroy.complete();
  }

  protected setInitialValue() {
    this.filteredMaterialesMulti
      .pipe(take(1), takeUntil(this._onDestroy))
      .subscribe(() => {

        this.multiSelect.compareWith = (a, b) => a && b && a === b;
      });
  }

  protected filterMaterialMulti() {
    if (!this.MaterialesBase) {
      return;
    }
    // get the search keyword
    let search = this.materialesMultiFilterCtrl.value;
    if (!search) {
      this.filteredMaterialesMulti.next(this.MaterialesBase.slice());
      return;
    } else {
      search = search.toLowerCase();
    }
    // filter the banks
    this.filteredMaterialesMulti.next(
      this.MaterialesBase.filter((materialBase: any) => materialBase.toLowerCase().indexOf(search) > -1)
    );
  }

  onClickAddMaterialBase() {

    const data = {
      _id : this.opcionesMaterialBaseId,
      data: this.materialesMultiFilterCtrl.value
    }

    this._opcionesInformesService.addOpcionesOrdenes(data).subscribe( (res:any)=>{
      Swal.fire({
        title: '¡Se agrego correctamente la opción!',
        icon: 'success',
        confirmButtonText: 'Cerrar',
      });

      this.onGetOpciones()
    })

  }


  forms() {
    this.Normas = this._formBuilder.group({
      criterio_aceptacion: ['', [Validators.required]],
      procedimiento: ['', [Validators.required]]
    });
    this.Juntas = this._formBuilder.group({
      juntas: ['']
    });
    this.ElementosInspeccionados = this._formBuilder.group({
      elementos_inspeccionados: ['']
    });
    this.Equipo_Utilizado = this._formBuilder.group({
      marca: ['SIUI', [Validators.required]],
      modelo: ['CTS 9006', [Validators.required]],
      escala: ['1:1', [Validators.required]],
      grado_calibracion: ['43-44', [Validators.required]],
      cable: ['Coaxial', [Validators.required]],
      metodo_ensayo: ['Scan A, Pulso -Eco', [Validators.required]],
      inspeccion: ['Inicial', [Validators.required]]
    });
    this.Palpador = this._formBuilder.group({
      tipo: ['Angular', [Validators.required]],
      angulo: ['70', [Validators.required]],
      frecuencia: ['2,5', [Validators.required]],
      dimension: ['20 mm x 20 mm', [Validators.required]]
    });
    this.Soldadura = this._formBuilder.group({
      proceso: ['SMAW', [Validators.required]],
      paso: ['66', [Validators.required]],
      paso_medio: ['33', [Validators.required]],
      rango: ['132', [Validators.required]]
    });
    this.Materiales = this._formBuilder.group({
      material_base: ['No especificado', [Validators.required]],
      espesor_mat_base: ['10', [Validators.required]],
      material_aporte: ['No especificado', [Validators.required]],
      acoplante: ['CMC', [Validators.required]]
    });
    this.RegistroFotosForm = this._formBuilder.group({
      title: ['', [Validators.required]],
    });
    this.descripcion = this._formBuilder.group({
      elemento: ['', [Validators.required]]
    });
    this.descripcionItem = this._formBuilder.group({
      junta: ['', [Validators.required]],
      ubicacion: ['', [Validators.required]],
      estampe: ['', [Validators.required]],
      decibeles_a: ['', [Validators.required]],
      decibeles_b: ['', [Validators.required]],
      decibeles_c: ['', [Validators.required]],
      decibeles_d: ['', [Validators.required]],
      dist: ['', [Validators.required]],
      prof_mm: ['', [Validators.required]],
      dist_y_mm: ['', [Validators.required]],
      dist_x_mm: ['', [Validators.required]],
      eval: ['', [Validators.required]]
    });

    this.observaciones = this._formBuilder.group({
      observaciones: ['', [Validators.required]]
    })
  }

