import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute, NavigationEnd } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import Swal from 'sweetalert2';
import { Observable } from 'rxjs'; // Importa Observable
import { catchError, switchMap } from 'rxjs/operators'; // Importa operadores de RxJS


/* SERVICES */

import { OrdenesService } from "../../service/ordenes/ordenes.service";
import { InformesOrdenesService } from "../../service/informes-ordenes/informes-ordenes.service"
import { FormsService } from "../../service/form/forms.service";


@Component({
  selector: 'app-informe-orden',
  templateUrl: './informe-orden.component.html',
  styleUrls: ['./informe-orden.component.css']
})
export class InformeOrdenComponent implements OnInit {

  /* PARAMETROS */
  orden_id: any;
  informe_orden_id: any;
  informe_id: any

  /* DETALLES */

  detallesOrden: any = [];
  detallesInforme: any

  /* FORMULARIO */

  form!: FormGroup;
  formData: any
  @Input() data: any = {};
  @Input() meta: any[] = [];

  /* FORMULARIO TIPO TABLA */

  datosTabla$: Observable<any[]>;



  constructor(private fb: FormBuilder, private _ordenesService: OrdenesService, private _activatedRoute: ActivatedRoute, private _informesOrdenesService: InformesOrdenesService, private _formService: FormsService,
  ) {

    this._activatedRoute.params.subscribe(params => {
      this.orden_id = params['orden_id'];
      this.informe_orden_id = params['informe_orden_id'];
      this.informe_id = params['informe_id']
    })
    this.form = this.fb.group({});
    this.datosTabla$ = this.onGetInformacionTablas();
  }



  ngOnInit(): void {
    this.onGetForms()
    this.onGetDetallesOrden();
  }

  /* OBTENER DETALLES */

  onGetDetallesOrden() {

    this._ordenesService.getDetallesOrdenes(this.orden_id).subscribe((res) => {

      this.detallesOrden = res.body[0]

    })

    this._informesOrdenesService.getDetallesInforme(this.informe_orden_id).subscribe((res) => {

      this.detallesInforme = res.body[0]

    })
  }

  /* FORMULARIO */

  toForm() {
    this.form = this.fb.group({});
    this.meta.forEach(step => {
      step.stepperGroup.forEach((field: any) => {
        this.form.addControl(field.key, this.fb.control(field.defaultValue));
      });
    });
  }

  onGetForms() {

    this._formService.GET(this.informe_id).subscribe((res: any) => {
      this.meta = res.body.form
      this.formData = res.body
      this.toForm()

      this.GetInforme()
    }, error => {
      console.log(error)
      var errorMessage = <any>error;
      if (errorMessage != null) {
        var body = error.error
        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });
        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        } else if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })

  }

  onSubmit() {

    let data = {
      informe: this.informe_id,
      orden_id: this.orden_id,
      informe_orden_id: this.informe_orden_id,
      data: this.form.value,
    }

    this._informesOrdenesService.POSTInformeDiligenciar(data).subscribe((res: any) => {

      if (res.status === 200) {
        Swal.fire({
          title: '¡El informe se ha guardado correctamente!',
          icon: 'success',
          confirmButtonText: 'Cerrar',
        });

        let element: HTMLElement = document.getElementsByClassName(
          'btn-close-add'
        )[0] as HTMLElement;
        element.click();

        this.GetInforme();
      }


    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });

        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        }
        if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })
  }

