  /* GUARDAR, LISTAR, MODIFICAR, ELIMINAR INFORMACIÓN DE LAS TABLAS DE LOS FORMULARIOS DE INFORMES */

  guardarDatosTable(step: any) {
    // Obtén los valores de entrada en la sección de tipo "Table"
    const datosSeccion: { [key: string]: any } = {};
    step.stepperGroup.forEach((field: any) => {
      datosSeccion[field.key] = this.form.get(field.key)?.value;
    });

    let data = {
      informe: this.informe_id,
      orden_id: this.orden_id,
      informe_orden_id: this.informe_orden_id,
      seccion_id: step.id,
      data: datosSeccion,
    }

    this._informesOrdenesService.POSTDataFormTable(data).subscribe((res: any) => {

      if (res.status === 200) {
        const Toast = Swal.mixin({
          position: 'top',
          toast: true,
          showConfirmButton: false,
          timer: 2000,
          timerProgressBar: true,
        });

        Toast.fire({
          icon: 'info',
          title: '¡Registro Exitoso!',
          text: 'información registrada con éxito'
        });


        // Limpia los campos de entrada después de guardar
        step.stepperGroup.forEach((field: any) => {
          this.form.get(field.key)?.reset();
        });
      }


    }, error => {
      var errorMessage = <any>error;

      if (errorMessage != null) {
        var body = error.error

        if (error.status === 404) {
          Swal.fire({
            title: body.body.message,
            icon: 'warning',
            confirmButtonText: 'Cerrar',
          });

        }

        if (error.status == 500) {

          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: body.body.message

          })

        }
        if (error.status == 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px'
          })

          Toast.fire({
            icon: 'error',
            title: 'Por favor compruebe su conexión de internet'

          })
        }
      }
    })


  }

