  onGetInformacionTablas(): Observable<any[]> { // Devuelve un Observable
    return this._informesOrdenesService.GETDataFormTable(this.orden_id, this.informe_orden_id)
      .pipe(
        catchError(error => {
          // Maneja errores si es necesario
          console.error('Error al obtener los datos de la tabla', error);
          return [];
        })
      );
  }

  onGetDataTableFilter(id: any) {
    return this.datosTabla$.pipe(
      switchMap(data => {
        // Filtra los datos cuando estén disponibles
        if (Array.isArray(data)) {
          const objetosFiltrados = data.filter(item => item.seccion_id === id);
          console.log(objetosFiltrados);
          return objetosFiltrados;
        } else {
          return []; // O devuelve un array vacío si no hay datos disponibles aún
        }
      })
    );
  }
