<div class="flex items-start justify-between">
  <div class="grid grid-cols-2 gap-x-4 gap-y-1" *ngIf="detallesOrden">
    <div class="text-4xl tracking-tight text-secondary">Proyecto</div>
    <div class="text-4xl">{{detallesOrden.proyecto}}</div>
    <div class="font-medium tracking-tight text-secondary">Sub-proyecto</div>
    <div class="font-medium">{{detallesOrden.subProyecto}}</div>
    <div class="font-medium tracking-tight text-secondary">Consecutivo</div>
    <div class="font-medium">{{detallesOrden.orden_consecutivo}}</div>
    <div class="font-medium tracking-tight text-secondary">Fecha de inicio</div>
    <div class="font-medium">{{detallesOrden.fecha_visita | date : 'mediumDate'}}</div>
    <div class="font-medium tracking-tight text-secondary">Lugar de inspección</div>
    <div class="font-medium">{{detallesOrden.lugar_visita}}</div>
  </div>
</div>

<div class="projects-section-header" *ngIf="detallesInforme">
  <div class="flex sm items-start sm:items-center sm:justify-between py-8 px-6 px-8">
    <div>
      <h3 class="text-3xl font-extrabold tracking-tight leading-none">{{detallesInforme.informe}}</h3>
    </div>
  </div>
</div>


<div class="container" *ngIf="meta">
  <form [formGroup]="form" (ngSubmit)="onSubmit()">
    <mat-vertical-stepper>
      <mat-step *ngFor="let step of meta; index as i; last as last" [stepControl]="form">
        <ng-template matStepLabel>{{ step.props.label }}</ng-template>
        <div class="card">
          <div class="card-body">
            <div *ngFor="let field of step.stepperGroup" class="flex">
              <app-control [control]="field" [form]="form" style="width: 100%;"></app-control>
            </div>



            <!-- SECCIÓN TABLA -->
            <div class="table-responsive" *ngIf="step.props.type === 'Table'">
              <button type="button"
                class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
                mat-flat-button type="button" (click)="guardarDatosTable(step)">
                Guardar
              </button>
              <table class="table table-bordered">
                <thead>
                  <tr>
                    <th scope="col" *ngFor="let field of step.stepperGroup">{{ field.label }}</th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let item of onGetDataTableFilter(step.id) | async">
                    <th></th>
                  </tr>
                  <tr>
                    <td [colSpan]="step.stepperGroup.length"  style="text-align: center;">
                      Aquí se incluirá la información de la tabla.</td>

                  </tr>
                </tbody>
              </table>


            </div>


            <!-- SECCIÓN DE IMAGENES   -->


            <div class="marging row-img" *ngIf="step.props.type === 'Image'">
              <div class="column-img">
                <mat-grid-list cols="2" rowHeight="2:1">
                  <mat-grid-tile>

                    <img mat-card-image class="img">


                    <mat-grid-tile-footer>
                      <h3 mat-line>Registro Fotográfico N°</h3>
                      <span mat-line> </span>
                      <button mat-mini-fab color="warn">
                        <mat-icon>restore_from_trash</mat-icon>
                      </button>
                    </mat-grid-tile-footer>

                  </mat-grid-tile>
                </mat-grid-list>
              </div>
            </div>


          </div>

        </div>
        <div class="flex justify-end btn-content">
          <button *ngIf="i > 0"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button [type]="last ? 'submit':'button'"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
            mat-flat-button type="button" matStepperNext>
            {{last ? 'Guardar':'Siguiente'}}
          </button>
        </div>
      </mat-step>
    </mat-vertical-stepper>
  </form>
</div>
