              icon: 'warning',
              confirmButtonText: 'Cerrar',
            });
          }

          if (error.status == 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
