            timerProgressBar: true,
            width: '300px',
          });

          Toast.fire({
            icon: 'error',
            title: body.body.message,
          });
        }

        if (error.status === 0) {
          const Toast = Swal.mixin({
            toast: true,
            position: 'center',
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
            width: '300px',
          });

