      elementos_inspeccionados: ['', [Validators.required, Validators.maxLength(50)]]
    });
    this.Interpretacion_evaluacion = this._formBuilder.group({
      elemento: ['', [Validators.required]],
      indicacion: ['', [Validators.required]],
      calificacion: ['', [Validators.required]],
      observaciones: ['', [Validators.required]]
    });
    this.Observaciones = this._formBuilder.group({
      observaciones: ['', [Validators.required]]
    });
    this.RegistroFotosForm = this._formBuilder.group({
      title: ['', [Validators.required]]
    });
  }

  onGetOpciones() {
    this._opcionesInformesService.getOpcionesOrdenes(this.informe_id).subscribe(res => {
      const result = res.body.filter(opciones => opciones.name === 'normas');
      this.opcionesNormas = result[0].data;
      this.opcionesNormasId = result[0]._id;
      this.onInitFilter();
    });
  }

  onInitFilter() {
    // set initial selection
    // load the initial bank list
    this.filteredNormasMulti.next(this.opcionesNormas.slice()); // listen for search field value changes

    this.normasMultiFilterCtrl.valueChanges.pipe(takeUntil(this._onDestroy)).subscribe(() => {
      this.filterMaterialMulti();
    });
  }

  ngAfterViewInit() {
    this.setInitialValue();
  }

  ngOnDestroy() {
    this._onDestroy.next();

    this._onDestroy.complete();
  }

  setInitialValue() {
    this.filteredNormasMulti.pipe(take(1), takeUntil(this._onDestroy)).subscribe(() => {
      this.multiSelect.compareWith = (a, b) => a && b && a === b;
    });
  }

  filterMaterialMulti() {
    if (!this.opcionesNormas) {
      return;
    } // get the search keyword


    let search = this.normasMultiFilterCtrl.value;

    if (!search) {
      this.filteredNormasMulti.next(this.opcionesNormas.slice());
      return;
    } else {
      search = search.toLowerCase();
    } // filter the banks


    this.filteredNormasMulti.next(this.opcionesNormas.filter(normas => normas.toLowerCase().indexOf(search) > -1));
  }

  onClickAddNormas() {
    const data = {
      _id: this.opcionesNormasId,
      data: this.normasMultiFilterCtrl.value
    };

    this._opcionesInformesService.addOpcionesOrdenes(data).subscribe(res => {
      Swal.fire({
        title: '¡Se agrego correctamente la opción!',
        icon: 'success',
        confirmButtonText: 'Cerrar'
      });
      this.onGetOpciones();
    });
  }

  onGet() {
    this.onGetAutoSave();
    this.onGetDetallesOrden();
    this.onGetNormas();
    this.onGetMaterialesUtilizados();
    this.onGetNormasProcess();
    this.onGetParametrosOp();
    this.onGetInterpretacion();
    this.onGetObservaciones();
    this.onGetRegistroFotos();
    this.onGetElementosInspeccionados();
    this.getFirmaElaboro();
    this.getFirmaReviso();
  }

  getImplementosOrdenes() {
    this._activatedRoute.params.subscribe(params => {
      // this.addImplemento.orden_id = orden_id
      const {
        orden_id,
        informe_id
      } = params;

      this._ordenesService.getImplementosOrdenes(orden_id).subscribe(res => {
        this.implementosOrdenes = res.body;
        console.log(this.implementosOrdenes);
      }, error => {
        var errorMessage = error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 404) {
            this.implementosOrdenes = [];
          }

          if (error.status == 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: body.body.message
            });
          } else if (error.status == 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'
            });
          }
        }
      });
    });
  }

  onGetAutoSave() {
    this._activatedRoute.params.subscribe(params => {
      const {
        orden_id,
        informe_id
      } = params;

      this._informesRutasService.getAutoSaveStepper(orden_id, informe_id).subscribe(res => {
        this.statusAutoSave = true;
        this.autoSaveIndex = res.body;
        console.log(res.body);
        this.windowOnloadCanvasElaboro();
        this.windowOnloadCanvasReviso();
      }, error => {
        var errorMessage = error;

        if (errorMessage != null) {
          var body = error.error;

          if (error.status === 404) {
            this.statusAutoSave = false;
            this.autoSaveIndex = body.body;
          } else if (error.status === 500) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: body.message
            });
          } else if (error.status === 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            });
            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'
            });
          }
        }
      });
    });
  }

