import { Component, ElementRef, OnInit, ViewChild, Inject, Injectable } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormControl} from '@angular/forms';
import { Router, ActivatedRoute, NavigationEnd } from '@angular/router';
import { ReplaySubject, Subject } from 'rxjs';
import { take, takeUntil } from 'rxjs/operators';
import { MatSelect } from '@angular/material/select';


import { AdherenciaComponent } from "../../components/adherencia/adherencia.component";


/* SERVICES */
import { OpcionesInformesService } from "../../service/opciones-informes/opciones-informes.service"
import { InformesRutasService } from "../../service/rutas-informes/informes-rutas.service";
import { OrdenesService } from "../../service/ordenes/ordenes.service";
import { LiquidosPenetrantesService } from 'src/app/service/liquidos_penetrantes/liquidos-penetrantes.service';
import { environment } from "../../../environments/environment";


import Swal from 'sweetalert2';

@Component({
  selector: 'app-liquidos-penetrantes',
  templateUrl: './liquidos-penetrantes.component.html',
  styleUrls: ['./liquidos-penetrantes.component.css'],
  providers: [AdherenciaComponent]
})
export class LiquidosPenetrantesComponent implements OnInit {

  url: string;

  orden_id: any
  informe_id: any

  @ViewChild('formFileSmRegistroFotos')
  myInputVariableRegistroFotos!: ElementRef;

  quillModules: any = {
    toolbar: [
      ['bold', 'italic', 'underline'],
      [{ align: [] }, { list: 'ordered' }, { list: 'bullet' }],
      ['clean']
    ]
  };



  NormasList = [
    'AWS D1.1 2020',
    'AWS D1.2 2003',
    'AWS D1.3 2008',
    'AWS D1.4 2005',
    'AASHTO/AWS D1.5 2010',
    'AWS D1.6 2007',
    'AWS D1.7 2010',
    'AWS D1.8 2009',
    'AWS D1.9 2007',
    'AWS D14.3 2010',
    'ASME SECCIÓN V 2010',
    'ASME SECCIÓN IX 2010',
    'ASME B31.1 2010',
    'ASME B31.3 2010',
    'ASME B31.4 2006',
    'ASME B31.5 2001',
    'ASME B31.8 2010',
    'ASME B31.9 2008',
    'ASME B31G 2009',
    'ASTM E 186',
    'API 1104',
    'Otro'
  ]

  /* OPCIONES NORMAS */
  opcionesNormas: any = []
  opcionesNormasId: any

  filteredNormasMulti: ReplaySubject<[]> = new ReplaySubject<[]>(1);
  normasMultiFilterCtrl: FormControl = new FormControl();
  protected _onDestroy = new Subject<void>();

  @ViewChild('multiSelect') multiSelect!: MatSelect;


  ocultoNoma: any
  ocultoProc: any
  ocultoMat: any
  ocultoNomProc: any
  ocultoTip: any
  ocultoMet: any
  ocultoP: any

  tiempoAc: any
  campoOtro = document.querySelector("#OtroPrc");

  public implementosOrdenes: any = []
  public addImplemento: any


  MaterialesBase = this._adherenciaComponent.MaterialesBase;


  detallesOrden: any
  detallesInforme: any

  isLinear = false;
  Normas!: FormGroup;
  Materiales_Utilizados!: FormGroup;
  Normas_ProcessModel!: FormGroup;
  Parametros_operacion!: FormGroup;
  ElementosInspeccionados!: FormGroup;
  Interpretacion_evaluacion!: FormGroup;
  Observaciones!: FormGroup;

  NormasId: any
  NormasBtnSave: boolean = true
  NormasStatus: boolean = false;

  MaterialesUtilizadosStatus: boolean = false;
  MaterialesUtilizadosId: any
  MaterialesUtilizados: any = []


  NormasProceId: any
  NormasProceBtnSave: boolean = true
  NormasProceStatus: boolean = false;

  oculto: any

  ParametrosOpStatus: boolean = false;
  ParametrosOpId: any
  ParametrosOp: any = []


  ElementosInspeccionadosId: any
  ElementosInspeccionadosBtnSave: boolean = true
  ElementosInspeccionadosStatus: boolean = false;


  InterpretacionEvaluacionStatus: boolean = false;
  InterpretacionEvaluacionId: any
  InterpretacionEvaluacion: any = []


  ObservacionesId: any
  ObservacionesBtnSave: boolean = true
  ObservacionesStatus: boolean = false;

  autoSaveIndex: any
  statusAutoSave: any

  //REGISTRO FOTOGRAFICOS

  imgSelectedRegistroFotos: any;
  fileRegistroFotos!: File;
  RegistroFotos: any = []
  showFlagRegistroFotos: boolean = false;
  selectedImageIndexRegistroFotos: number = -1;
  currentIndexRegistroFotos: any
  imageObjectRegistroFotos: Array<object> = []
  RegistroFotosForm!: FormGroup


  ResultadosStatus: boolean = false;

  //firmas
  canvas: any
  firmaRevisaImg: String = ''
  firmaRevisoStatus: boolean = false


  firmaElaboroImg: String = '';
  firmaElaboroStatus: boolean = false

  @ViewChild('firmaElaboro')
  canvasElaboroRef!: ElementRef;



  constructor(
    private _formBuilder: FormBuilder, private _route: Router,
    private _activatedRoute: ActivatedRoute,
    private _adherenciaComponent: AdherenciaComponent,
    private _ordenesService: OrdenesService,
    private _liquidos_penetrantes: LiquidosPenetrantesService,
    private _informesRutasService: InformesRutasService,
    private _opcionesInformesService: OpcionesInformesService
  ) {

    this.url = environment.url;
    this._activatedRoute.params.subscribe(params => {
      this.orden_id = params['orden_id'];
      this.informe_id = params['informe_id'];

    })
  }

  ngOnInit(): void {
    this.getImplementosOrdenes()
    this.forms()
    this.onGetDetallesOrden()
    this.onGet()

    this.onGetOpciones()
  }

  forms() {
    this.Normas = this._formBuilder.group({
      norma: ['', [Validators.required]],
      procedimiento: ['', [Validators.required]],
      esp_material_base: ['', [Validators.required]],
      process_soldadura: ['', [Validators.required]],
      equipos_utilizados: ['', [Validators.required]]
    });
    this.Materiales_Utilizados = this._formBuilder.group({
      detalles: ['', [Validators.required]],
      fabricante: ['', [Validators.required]],
      ref_comercial: ['', [Validators.required]],
      lotes_n: ['', [Validators.required]],
    });
    this.Normas_ProcessModel = this._formBuilder.group({
      normas_process: ['', [Validators.required]],
      tipo: ['', [Validators.required]],
      metodo: ['', [Validators.required]],
      procedimiento: [' Limpieza de la superficie con método manual (con paño). 2° Aplicación de limpiador. 3° Aplicación del líquido penetrante, dejándose actuar durante 10:00 min. 4° Limpieza del líquido con papel absorbente. 5° Aplicación de revelador, dejándose actuar durante 10:00 min para luego realizar la interpretación. 6° limpieza final', [Validators.required]]
    });
    this.Parametros_operacion = this._formBuilder.group({
      actividad: ['', [Validators.required]],
      tiempo: ['', [Validators.required]],
      temperatura: ['', [Validators.required]],
      aplicacion: ['', [Validators.required]],
      iluminacion: ['', [Validators.required]]
    });
    this.ElementosInspeccionados = this._formBuilder.group({
      elementos_inspeccionados: ['', [Validators.required, Validators.maxLength(50)]],
    });
    this.Interpretacion_evaluacion = this._formBuilder.group({
      elemento: ['', [Validators.required]],
      indicacion: ['', [Validators.required]],
      calificacion: ['', [Validators.required]],
      observaciones: ['', [Validators.required]]
    });
    this.Observaciones = this._formBuilder.group({
      observaciones: ['', [Validators.required]]
    })

    this.RegistroFotosForm = this._formBuilder.group({
      title: ['', [Validators.required]],
    });

  }

  onGetOpciones() {
    this._opcionesInformesService.getOpcionesOrdenes(this.informe_id).subscribe((res) => {

      const result = res.body.filter((opciones: any) => opciones.name === 'normas')

      this.opcionesNormas = result[0].data

      this.opcionesNormasId = result[0]._id

      this.onInitFilter()

    })
  }

  onInitFilter() {
    // set initial selection


    // load the initial bank list
    this.filteredNormasMulti.next(this.opcionesNormas.slice());

    // listen for search field value changes
    this.normasMultiFilterCtrl.valueChanges
      .pipe(takeUntil(this._onDestroy))
      .subscribe(() => {
        this.filterMaterialMulti();
      });
  }

  ngAfterViewInit() {
    this.setInitialValue();
  }

  ngOnDestroy() {
    this._onDestroy.next();
    this._onDestroy.complete();
  }

  protected setInitialValue() {
    this.filteredNormasMulti
      .pipe(take(1), takeUntil(this._onDestroy))
      .subscribe(() => {

        this.multiSelect.compareWith = (a, b) => a && b && a === b;
      });
  }

  protected filterMaterialMulti() {
    if (!this.opcionesNormas) {
      return;
    }
    // get the search keyword
    let search = this.normasMultiFilterCtrl.value;
    if (!search) {
      this.filteredNormasMulti.next(this.opcionesNormas.slice());
      return;
    } else {
      search = search.toLowerCase();
    }
    // filter the banks
    this.filteredNormasMulti.next(
      this.opcionesNormas.filter((normas: any) => normas.toLowerCase().indexOf(search) > -1)
    );
  }

  onClickAddNormas() {

    const data = {
      _id : this.opcionesNormasId,
      data: this.normasMultiFilterCtrl.value
    }

    this._opcionesInformesService.addOpcionesOrdenes(data).subscribe( (res:any)=>{
      Swal.fire({
        title: '¡Se agrego correctamente la opción!',
        icon: 'success',
        confirmButtonText: 'Cerrar',
      });

      this.onGetOpciones()
    })

  }

  onGet() {
    this.onGetAutoSave()
    this.onGetDetallesOrden()
    this.onGetNormas()
    this.onGetMaterialesUtilizados()
    this.onGetNormasProcess()
    this.onGetParametrosOp()
    this.onGetInterpretacion()
    this.onGetObservaciones()
    this.onGetRegistroFotos()
    this.onGetElementosInspeccionados()
    this.getFirmaElaboro()
    this.getFirmaReviso()
  }

  getImplementosOrdenes() {
    this._activatedRoute.params.subscribe(params => {
      // this.addImplemento.orden_id = orden_id

      const { orden_id, informe_id } = params;

      this._ordenesService.getImplementosOrdenes(orden_id).subscribe((res: any) => {

        this.implementosOrdenes = res.body

        console.log(this.implementosOrdenes)

      }, error => {
        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.implementosOrdenes = []
          }

          if (error.status == 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.body.message

            })

          } else if (error.status == 0) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })
            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'
            })
          }
        }
      })

    })
  }

  onGetAutoSave() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._informesRutasService.getAutoSaveStepper(orden_id, informe_id).subscribe((res) => {

        this.statusAutoSave = true

        this.autoSaveIndex = res.body

        console.log(res.body)

        this.windowOnloadCanvasElaboro()
        this.windowOnloadCanvasReviso()

      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            this.statusAutoSave = false
            this.autoSaveIndex = body.body
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })

    })
  }

  setIndex(event: any) {


    if (this.statusAutoSave == true) {
      let data = {
        indexStepper: event.selectedIndex
      }

      this._informesRutasService.putAutoSaveStepper(data, this.autoSaveIndex._id).subscribe((res) => {
        this.onGetAutoSave()
      }, error => {

        var errorMessage = <any>error;

        if (errorMessage != null) {
          var body = error.error

          if (error.status === 404) {
            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'info',
              title: body.message
            })
          } else if (error.status === 500) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: body.message

            })

          } else if (error.status === 0) {

            const Toast = Swal.mixin({
              toast: true,
              position: 'center',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              width: '300px'
            })

            Toast.fire({
              icon: 'error',
              title: 'Por favor compruebe su conexión de internet'

            })

          }
        }
      })
    } else if (this.statusAutoSave == false) {
      this._activatedRoute.params.subscribe(params => {
        const { orden_id, informe_id } = params;


        let data = {
          indexStepper: event.selectedIndex,
          orden_id,
          informe_id
        }

        this._informesRutasService.postAutoSaveStepper(data).subscribe((res) => {
          this.onGetAutoSave()
        }, error => {

          var errorMessage = <any>error;

          if (errorMessage != null) {
            var body = error.error

            if (error.status === 404) {
              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'info',
                title: body.message
              })
            } else if (error.status === 500) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: body.message

              })

            } else if (error.status === 0) {

              const Toast = Swal.mixin({
                toast: true,
                position: 'center',
                showConfirmButton: false,
                timer: 4000,
                timerProgressBar: true,
                width: '300px'
              })

              Toast.fire({
                icon: 'error',
                title: 'Por favor compruebe su conexión de internet'

              })

            }
          }
        })

      })
    }
  }

  onGetDetallesOrden() {
    this._activatedRoute.params.subscribe(params => {
      const orden_id = params['orden_id'];
      const informe_id = params['informe_id'];

      this._ordenesService.getDetallesOrdenes(orden_id).subscribe((res) => {

        this.detallesOrden = res.body[0]

      })

      this._liquidos_penetrantes.getDetallesInforme(informe_id).subscribe((res) => {

        this.detallesInforme = res.body[0]

        // console.log(res.body)

        // this.NormasId = res.body._id

      })
    })
  }

  onChangePro(Opcion: any) {


    let selec = document.querySelector("#Prc");
    let campoOtro = document.querySelector("#OtroPrc");


    if (Opcion == "Otro") {
      this.oculto = 1;
    }
    else {
      this.oculto = 0;
    }

  }




  onGetNormas() {
    this._activatedRoute.params.subscribe(params => {
      const { orden_id, informe_id } = params;

      this._liquidos_penetrantes.getNormasLiquidos(orden_id, informe_id).subscribe((res: any) => {

        this.NormasId = res.body._id

        console.log('normas', res.body)

        this.Normas.patchValue(res.body)

        this.NormasBtnSave = false


