  finalizarInforme() {
    Swal.fire({

      title: "¿Está seguro de finalizar el informe?",
      text: "¡Si no lo está puede cancelar la acción!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      cancelButtonText: 'Cancelar',
      confirmButtonText: 'Si, finalizar el informe!'

    }).then((result) => {

      if (result.value) {

        this._liquidos_penetrantes.putFinalizarInforme(this.informe_id).subscribe((res: any) => {

          Swal.fire({
            title: '¡El informe se finalizo correctamente!',
            icon: 'success',
            confirmButtonText: 'Cerrar',
          });
        },
          error => {
            var errorMessage = <any>error;

            if (errorMessage != null) {
              var body = error.error

              if (error.status == 404) {
                Swal.fire({
                  title: body.body.message,
                  icon: 'warning',
                  confirmButtonText: 'Cerrar',
                });
              }

              if (error.status == 500) {

                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: body.body.message

                })

              } else if (error.status == 0) {
                const Toast = Swal.mixin({
                  toast: true,
                  position: 'center',
                  showConfirmButton: false,
                  timer: 4000,
                  timerProgressBar: true,
                  width: '300px'
                })

                Toast.fire({
                  icon: 'error',
                  title: 'Por favor compruebe su conexión de internet'

                })
              }
            }
          });
      }
    })

  }

