            <mat-select [formControlName]="'tipo'" (selectionChange)="onChangeTipo($event.value)" id="Tip">
              <mat-option [value]="'TIPO I: Tintas fluorescentes'">TIPO I: Tintas fluorescentes</mat-option>
              <mat-option [value]="'TIPO II: Tintas Visibles'">TIPO II: Tintas Visibles</mat-option>
              <mat-option [value]="'Otro'">Otro</mat-option>
            </mat-select>

            <!-- <input matInput [placeholder]="'Tipo'" [formControlName]="'tipo'" autocomplete="off" required /> -->
          </mat-form-field>

          <mat-form-field class="flex-auto" appearance="outline" *ngIf="ocultoTip==1">
            <input matInput [placeholder]="'Ingrese el tipo'" [formControlName]="'tipo'" autocomplete="off" id="OtroTip"
              value="">
          </mat-form-field>


          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Método</mat-label>
            <mat-select [formControlName]="'metodo'" (selectionChange)="onChangeMet($event.value)" id="Met">
              <mat-option [value]="'MÉTODO A: Lavable con agua'">MÉTODO A: Lavable con agua</mat-option>
              <mat-option [value]="'MÉTODO B: Post emulsificable, lipofilico'">MÉTODO B: Post emulsificable,
                lipofilico</mat-option>
              <mat-option [value]="'MÉTODO C: Removible con Solvente'">MÉTODO C: Removible con Solvente</mat-option>
              <mat-option [value]="'MÉTODO D: Post emulsificable, hidrofilico'">MÉTODO D: Post emulsificable,
                hidrofilico</mat-option>
              <mat-option [value]="'Otro'">Otro</mat-option>
            </mat-select>
            <!-- <input matInput [placeholder]="'Método'" [formControlName]="'metodo'" autocomplete="off" required /> -->
          </mat-form-field>

          <mat-form-field class="flex-auto" appearance="outline" *ngIf="ocultoMet==1">
            <input matInput [placeholder]="'Ingrese el método'" [formControlName]="'metodo'" autocomplete="off"
              id="OtroMet" value="">
          </mat-form-field>

        </div>


        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Procedimiento</mat-label>
            <mat-select [formControlName]="'procedimiento'" (selectionChange)="onChangeP($event.value)" id="Prd">
              <mat-option
                [value]="' Limpieza de la superficie con método manual (con paño). 2° Aplicación de limpiador. 3° Aplicación del líquido penetrante, dejándose actuar durante 10:00 min. 4° Limpieza del líquido con papel absorbente. 5° Aplicación de revelador, dejándose actuar durante 10:00 min para luego realizar la interpretación. 6° limpieza final'">
                Limpieza de la superficie con método manual (con paño). 2° Aplicación de limpiador. 3° Aplicación del
                líquido penetrante, dejándose actuar durante 10:00 min. 4° Limpieza del líquido con papel absorbente. 5°
                Aplicación de revelador, dejándose actuar durante 10:00 min para luego realizar la interpretación. 6°
                limpieza final</mat-option>
              <mat-option [value]="'Otro'">Otro</mat-option>
            </mat-select>

            <!-- <textarea matInput [placeholder]="'Procedimiento'" [formControlName]="'procedimiento'" autocomplete="off"
              required></textarea> -->
          </mat-form-field>
        </div>

        <div class="flex" *ngIf="ocultoP==1">
          <mat-form-field class="flex-auto" appearance="outline">
            <textarea matInput [placeholder]="'Ingrese el procedimiento'" [formControlName]="'procedimiento'"
              autocomplete="off" autocomplete="off" id="OtroPrd" required></textarea>
            <!-- <input  matInput  [placeholder]="'Ingrese la norma'" [formControlName]="'normas_process'" autocomplete="off" id="OtraNorm" value=""> -->
          </mat-form-field>
        </div>

        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="submit" mat-button matStepperNext matStepperNext
            (click)="NormasProceId ? onEditNormasProce(): onSubmitNormasProce()"
            [disabled]="Normas_ProcessModel.invalid"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save">
            <span class="mat-button-wrapper"> Siguiente </span>
            <span matripple class="mat-ripple mat-button-ripple"></span>
            <span class="mat-button-focus-overlay"></span></button>
        </div>
      </form>
    </mat-step>

    <mat-step [stepControl]="Parametros_operacion">
      <form [formGroup]="Parametros_operacion">
        <ng-template matStepLabel>Parámetros de operación</ng-template><br>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Actividad</mat-label>
            <mat-select [formControlName]="'actividad'" (selectionChange)="onChangeActividad($event.value)" id="Activ">
              <mat-option [value]="'Limpieza'">Limpieza</mat-option>
              <mat-option [value]="'Penetrante'">Penetrante</mat-option>
              <mat-option [value]="'Limpieza2'">Limpieza</mat-option>
              <mat-option [value]="'Revelador'">Revelador</mat-option>
            </mat-select>

            <!-- <input matInput [formControlName]="'actividad'" [placeholder]="'Actividad'" autocomplete="off" required> -->
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Tiempo de permanencia</mat-label>
            <input matInput [formControlName]="'tiempo'" [placeholder]="'Tiempo de permanencia'" autocomplete="off"
              id="TiempPe" [value]="'tieesp'">
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Temperatura</mat-label>
            <input matInput [formControlName]="'temperatura'" [placeholder]="'Temperatura'" autocomplete="off" required>
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Aplicación</mat-label>
            <mat-select [formControlName]="'aplicacion'">
              <mat-option [value]="'Spray'">Spray</mat-option>
              <mat-option [value]="'Sumergible'">Sumergible</mat-option>
            </mat-select>

            <!-- <input matInput [formControlName]="'aplicacion'" [placeholder]="'Aplicación'" autocomplete="off" required> -->
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Iluminación</mat-label>
            <mat-select [formControlName]="'iluminacion'">
              <mat-option [value]="'Luz natural'">Luz natural</mat-option>
              <mat-option [value]="'Luz negra'">Luz negra</mat-option>
            </mat-select>
            <!-- <input matInput [formControlName]="'iluminacion'" [placeholder]="'Iluminación'" autocomplete="off" required> -->
          </mat-form-field>
        </div>

        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" (click)="cancelarEditMaterialesUtilizados()"
            *ngIf="MaterialesUtilizadosStatus">
            Cancela
          </button>
          <button type="submit"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base  btn btn-success"
            mat-flat-button type="button"
            (click)="ParametrosOpStatus == false ? onSubmitParametrosOp() : onEditarParametrosOp()"
            [disabled]="Parametros_operacion.status != 'VALID'">
            {{ParametrosOpStatus== false ? 'Añadir' : 'Editar'}}
          </button>
        </div>
        <table class="table table-hover">
          <tbody>
            <tr *ngFor="let itemParametros of ParametrosOp">
              <td>{{itemParametros.actividad }}</td>
              <td>{{itemParametros.tiempo}}</td>
              <td>{{itemParametros.temperatura}}</td>
              <td>{{itemParametros.aplicacion }}</td>
              <td>{{itemParametros.iluminacion }}</td>

              <td>
                <div class="dropdown">
                  <button class="btn btn-sm btn-icon" type="button" role="button" id="dropdownMenuLink"
                    data-bs-toggle="dropdown" aria-expanded="false">
                    <i class="fas fa-ellipsis-h" data-toggle="tooltip" data-placement="top" title=""
                      data-original-title="Actions"></i>
                  </button>
                  <ul class="dropdown-menu" aria-labelledby="dropdownMenuLink">
                    <li>
                      <a class="dropdown-item" (click)="onClickEditMaterialesUtilizados(itemParametros)"><i
                          style="color: rgb(26, 25, 25);" class="fas fa-pencil-alt"></i>
                        &nbsp; <strong>Editar</strong>
                      </a>
                    </li>
                    <li>
                      <a class="dropdown-item text-danger" (click)='onDeleteParametrosOp(itemParametros._id)'
                        style="cursor: pointer;">
                        <i style="color: rgb(148, 21, 21);" class="fas fa-trash"></i> &nbsp;
                        <strong>Eliminar</strong>
                      </a>
                    </li>
                  </ul>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="button"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
            mat-flat-button type="button" matStepperNext>
            Siguiente
          </button>
        </div>
      </form>
    </mat-step>

    <mat-step [stepControl]="ElementosInspeccionados">
      <form [formGroup]="ElementosInspeccionados">
        <ng-template matStepLabel>
          <div class="mat-step-custom-click"></div>
          Elementos inspeccionados
        </ng-template>
        <quill-editor placeholder="Elementos Inspeccionados" [formControlName]="'elementos_inspeccionados'"
          [modules]="quillModules">
        </quill-editor>

        <div class="flex justify-end mt-3">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="submit" mat-button matStepperNext [disabled]="ElementosInspeccionados.invalid"
            (click)="ElementosInspeccionadosId ?  onEditarElementosInspeccionados() : onSubmitElementosInspeccionados()"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save">
            <span class="mat-button-wrapper"> Siguiente </span>
            <span matripple class="mat-ripple mat-button-ripple"></span>
            <span class="mat-button-focus-overlay"></span></button>
        </div>


      </form>
    </mat-step>

    <mat-step [stepControl]="Interpretacion_evaluacion">
      <form [formGroup]="Interpretacion_evaluacion">
        <ng-template matStepLabel>Interpretacion y evaluación de resultados</ng-template><br>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Elemento</mat-label>
            <input matInput [formControlName]="'elemento'" [placeholder]="'Elemento'" autocomplete="off" required>
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Indicación</mat-label>

            <mat-select [formControlName]="'indicacion'">
              <mat-option [value]="'Cir'">Cordón Irregular</mat-option>
              <mat-option [value]="'EC'">Exceso de Concavidad</mat-option>
              <mat-option [value]="'ER'">Exceso de Refuerzo</mat-option>
              <mat-option [value]="'PAG'">Porosidad Agrupada</mat-option>
              <mat-option [value]="'FF'">Falta de Fusión</mat-option>
              <mat-option [value]="'FP'">Falta de Penetración</mat-option>
              <mat-option [value]="'G'">Grietas</mat-option>
              <mat-option [value]="'EP'">Exceso de Penetración</mat-option>
              <mat-option [value]="'P'">Porosidad</mat-option>
              <mat-option [value]="'SE'">Socavado Externo</mat-option>
              <mat-option [value]="'SI'">Socavado Interno</mat-option>
              <mat-option [value]="'FMA'">Falta de Material de aporte</mat-option>
              <mat-option [value]="'DMA'">Daño Material Base</mat-option>
            </mat-select>

          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Calificación</mat-label>
            <mat-select [formControlName]="'calificacion'">
              <mat-option [value]="'C'">Conforme</mat-option>
              <mat-option [value]="'C(xR)'">Conforme luego x Reparación</mat-option>
              <mat-option [value]="'NC'">No conforme</mat-option>
              <mat-option [value]="'I'">Inspeccionar</mat-option>
            </mat-select>
          </mat-form-field>
        </div>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Observaciones</mat-label>
            <input matInput [formControlName]="'observaciones'" [placeholder]="'Observaciones'" autocomplete="off"
              required>
          </mat-form-field>
        </div>


        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" (click)="cancelarEditMaterialesUtilizados()"
            *ngIf="MaterialesUtilizadosStatus">
            Cancela
          </button>
          <button type="submit"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base  btn btn-success"
            mat-flat-button type="button"
            (click)="InterpretacionEvaluacionStatus == false ? onSubmitInterpretacion() : onEditarInterpretacion()"
            [disabled]="Interpretacion_evaluacion.status != 'VALID'">
            {{InterpretacionEvaluacionStatus== false ? 'Añadir' : 'Editar'}}
          </button>
        </div>
        <table class="table table-hover">
          <tbody>
            <tr *ngFor="let itemInter of InterpretacionEvaluacion">
              <td>{{itemInter.item }}</td>
              <td>{{itemInter.elemento }}</td>
              <td>{{itemInter.indicacion}}</td>
              <td>{{itemInter.calificacion}}</td>
              <td>{{itemInter.observaciones }}</td>
              <td>
                <div class="dropdown">
                  <button class="btn btn-sm btn-icon" type="button" role="button" id="dropdownMenuLink"
                    data-bs-toggle="dropdown" aria-expanded="false">
                    <i class="fas fa-ellipsis-h" data-toggle="tooltip" data-placement="top" title=""
                      data-original-title="Actions"></i>
                  </button>
                  <ul class="dropdown-menu" aria-labelledby="dropdownMenuLink">
                    <li>
                      <a class="dropdown-item" (click)="onClickEditInterpretacionEvaluacion(itemInter)"><i
                          style="color: rgb(26, 25, 25);" class="fas fa-pencil-alt"></i>
                        &nbsp; <strong>Editar</strong>
                      </a>
                    </li>
                    <li>
                      <a class="dropdown-item text-danger" (click)='onDeleteInterpretacion(itemInter._id)'
                        style="cursor: pointer;">
                        <i style="color: rgb(148, 21, 21);" class="fas fa-trash"></i> &nbsp;
                        <strong>Eliminar</strong>
                      </a>
                    </li>
                  </ul>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="button"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
            mat-flat-button type="button" matStepperNext>
            Siguiente
          </button>
        </div>
      </form>
    </mat-step>

    <mat-step [stepControl]="Observaciones">
      <form [formGroup]="Observaciones">
        <ng-template matStepLabel>
          <div class="mat-step-custom-click"></div>
          Observaciones
        </ng-template>
        <quill-editor placeholder="Observaciones" [formControlName]="'observaciones'" [modules]="quillModules">
        </quill-editor>

        <div class="flex justify-end mt-3">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="submit" mat-button matStepperNext [disabled]="Observaciones.invalid"
            (click)="ObservacionesId ?  onEditarObservaciones() : onSubmitObservaciones()"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save">
            <span class="mat-button-wrapper"> Siguiente </span>
            <span matripple class="mat-ripple mat-button-ripple"></span>
            <span class="mat-button-focus-overlay"></span></button>
        </div>


      </form>
    </mat-step>

    <mat-step [stepControl]="RegistroFotosForm">
      <form [formGroup]="RegistroFotosForm">
        <ng-template matStepLabel>
          <div class="mat-step-custom-click" (click)="onGetRegistroFotos()"></div>
          Registro Fotográfico
        </ng-template>
        <br>
        <div class="flex">
          <mat-form-field class="flex-auto" appearance="outline">
            <mat-label>Titulo</mat-label>
            <input matInput [formControlName]="'title'" [placeholder]="'Titulo'" autocomplete="off" required>
            <mat-icon class="icon-size-5 icon-form" matPrefix>title</mat-icon>
          </mat-form-field>
        </div>
        <div class="content-inputs">
          <div class="mb-3">
            <label for="formFileSm" class="form-label">Seleccion las imagenes</label>
            <input class="form-control form-control-sm" (change)="onPhotoSelectedRegistroFotos($event)"
              id="formFileSmRegistroFotos" type="file" accept="image/x-png,image/jpeg,image/jpg"
              #formFileSmRegistroFotos>
          </div>
        </div>
        <div class="flex justify-end mb-3">
          <button type="submit"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base  btn btn-success bt-m"
            mat-flat-button type="submit" (click)="onSubmitRegistroFotos()">
            Añadir
          </button>
        </div>

        <div class="marging row-img">
          <div class="column-img">
            <mat-grid-list cols="2" rowHeight="2:1">
              <mat-grid-tile *ngFor="let item of RegistroFotos; index as i;">

                <img mat-card-image [src]="url+'/img-uploads/' + item.image" alt="{{item.title}}"
                  (click)="showLightboxRegistroFotos(i)" class="img">


                <mat-grid-tile-footer>
                  <h3 mat-line>Registro Fotográfico N° {{i+1}}</h3>
                  <span mat-line> {{item.title}} </span>
                  <button mat-mini-fab color="warn" (click)="deleteRegistroFoto(item._id)">
                    <mat-icon>restore_from_trash</mat-icon>
                  </button>
                </mat-grid-tile-footer>

              </mat-grid-tile>
            </mat-grid-list>
          </div>
        </div>
        <ng-image-fullscreen-view [images]="imageObjectRegistroFotos" [imageIndex]="selectedImageIndexRegistroFotos"
          [show]="showFlagRegistroFotos" (close)="closeEventHandlerRegistroFotos()"></ng-image-fullscreen-view>
        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="button"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
            mat-flat-button type="button" matStepperNext>
            Siguiente
          </button>
        </div>
      </form>
    </mat-step>

    <mat-step>
      <form >
        <ng-template matStepLabel>
          <div class="mat-step-custom-click" (click)="onGetRegistroFotos()"></div>
          Firmas
        </ng-template>
        <br>

        <div class="flex">

          <div class="form-group">

            <label for="firma-elaboro">Elaboró</label>

            <div [style]="{'display': firmaElaboroStatus == true ? 'none' : 'block' }">

              <canvas id="firmaElaboro" class="firma" width="466" height="154" #firmaElaboro>
              </canvas>

              <div class="btn-toolbar">
                <div class="me-2">
                  <button type="button" id="btn-finalizar-firma-elaboro" class="btn btn-outline-success">Finalizar
                    firma</button>
                </div>
                <div class="me-2">
                  <button type="button" class="btn btn-secondary float-sm-right"
                    id="draw-clearBtn-firma-elaboro">Limpiar firma</button>
                </div>
              </div>
            </div>

            <div [style]="{'display': firmaElaboroStatus == true ? 'block' : 'none' }">

              <img src="{{firmaElaboroImg}}" width="466" height="154">

            </div>

          </div>

          <div class="form-group">
            <label for="firma-reviso">Revisó</label>
            <div [style]="{'display': firmaRevisoStatus == true ? 'none' : 'block' }">

              <canvas id="firmaReviso" class="firma" width="466" height="154">
              </canvas>

              <div class="btn-toolbar">
                <div class="me-2">
                  <button type="button" id="btn-finalizar-firma-reviso" class="btn btn-outline-success">Finalizar
                    firma</button>
                </div>
                <div class="me-2">
                  <button type="button" class="btn btn-secondary float-sm-right" id="draw-clearBtn-firma-reviso">Limpiar
                    firma</button>
                </div>
              </div>
            </div>
            <div [style]="{'display': firmaRevisoStatus == true ? 'block' : 'none' }">

              <img src="{{firmaRevisaImg}}" width="466" height="154">

            </div>
          </div>
        </div>

        <div class="flex justify-end">
          <button
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary back mr-2"
            mat-flat-button type="button" matStepperPrevious>
            Atras
          </button>
          <button type="button"
            class="mat-focus-indicator mat-stepper-next px-8 mat-flat-button mat-button-base mat-primary next-save"
            mat-flat-button type="button" (click)="finalizarInforme()" matStepperNext>
            Finalizar
          </button>
        </div>
      </form>
    </mat-step>

  </mat-stepper>
</form>
